(function ($) {
    
    $.fn.bindFacebookShare = function (args) {
        new SocialShareHelper(this, SocialShare.networks.FACEBOOK, args);
        return this;
    };
    $.fn.bindTwitterShare = function (args) {
        new SocialShareHelper(this, SocialShare.networks.TWITTER, args);
        return this;
    };
    $.fn.bindPinterestShare = function (args) {
        new SocialShareHelper(this, SocialShare.networks.PINTEREST, args);
        return this;
    };

})(jQuery);


// litebox stuff
function removeOverlay(litebox) {
        $('.blackout').fadeOut(250);
        $(litebox).hide().css({
                'width': '0',
                'height': '0'
        }).children('.inner').css('display', 'none');
        //$('body').css('overflow', 'auto');
}

function showOverlay(litebox, w, h) {
        $('.blackout').fadeIn(250);
        $(litebox).show().animate({
                width: w,
                height: h
        }, 300, function() {
                $(litebox).children('.inner').each(function() {
                        $(this).fadeIn(100);
                });
        });
        $('body').css('overflow', 'hidden');
}

function litebox_switch(lb1, lb2, lb2w, lb2h) 
{
        console.log("aiai")
        $(lb1).animate({
                width: '0',
                height: '0'
        }, 100, function() {
                $(lb1).hide().children('.inner').hide();
                $(lb2).show().animate({
                        width: lb2w,
                        height: lb2h
                }, 200, function() {
                        $(lb2).children('.inner').each(function() {
                                $(this).fadeIn(100);
                        });
                });
        });
}